import React from "react";

class Content extends React.Component   {
    contentCall() {
        fetch("http://localhost:8000/")
          .then(response => {
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          .then(data => {
            // Burada data kullanılmalı, örneğin:
            console.log(data);
          })
          .catch(error => {
            console.error('Fetch error:', error);
          });
      }
    render(){
        const content = this.contentCall();
        return(

            <div className="container mt-5">
                      <div className="row">

                              <div className="col-4">

                                  {/* <img src="/static/images/<%= products[i].picture%>" width="350" height="350"> */}
                                  <div style="margin-top: 40px;"><h5>name:{ content[0].name }</h5></div>
                                  <div><h5>price:{content[0].price }</h5></div>

                              <div>
                                  <button className="btn btn-warning mt-4" type="submit"> <a href="http://localhost:8000/productdetail/<%= products[i].id %>">See details</a></button>
                                  <button className="btn btn-outline-warning mt-4"> <a href="http://localhost:8000/buy/<%= products[i].id %>"> BUY</a></button>
                                  <button className="btn btn-info mt-4"><a href="http://localhost:8000/cart/<%= products[i].id %> "> Add to cart</a></button>
                                  <button className="btn  mt-4"><a href="http://localhost:8000/item/delete/<%= products[i].id %> "> delete from db</a></button>
                              </div>

                              </div>                    
                      </div>
                      </div>



        );
    }
}






export default Content;
