import React from "react";


class Content extends React.Component   {
  constructor() {
    super();
    this.state = {
      content: null,
    };
  }
  componentDidMount() {
    this.contentCall();
  }
  async contentCall() {
    try {
      const response = await fetch("http://localhost:8000/");

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      this.setState({ content: data });
    } catch (error) {
      console.error('Fetch error:', error);
    }
  }
  render() {
    console.log(this.state.content);

    return (
      <div>


        {this.state.content !== null && (
          <div className="container">
            <div className="row">
            {(
              this.state.content.map(item => (
                <div className="col-4" key={item.id}>
                  <img src={require(`../../../public/images/${item.picture}`)} alt="" />
                  <p>{item.name}</p>
                  <p>{item.price}</p>
                  <p>{item.description}</p>
                  <div>
                    <button class="btn btn-warning mt-4" type="submit"> <a href="http://localhost:8000/productdetail/<%= products[i].id %>">See details</a></button>
                    <button class="btn btn-outline-warning mt-4"> <a href={`http://localhost:8000/buy/${item.id}`}> BUY</a></button>
                    <button class="btn btn-info mt-4"><a href={`http://localhost:8000/cart/${item.id}`}> Add to cart</a></button>
                    <button class="btn  mt-4"><a href={`http://localhost:8000/item/delete/${item.id}`}> delete from db</a></button>
                </div>
                </div>
              ))
            ) }
            </div>
          </div>
        )}
      </div>
    );
  }

}






export default Content;
